RGB={r=0,g=0,b=0}function RGB:new(r,g,b)local o={} o.r=r o.g=g o.b=b return o end Button={text="default",px=0,py=0,sx=0,sy=0,state=false,bg=RGB:new(10,10,10),fg=RGB:new(200,200,200)}function Button.new(text,px,py,sx,sy)local o={}o.text=text or"default"o.px=px o.py=py o.sx=sx o.sy=sy o.bg=RGB:new(10,10,10)o.fg=RGB:new(200,200,200)o.draw=Button.draw return o end function Button.draw(self)local o = self screen.setColor(o.bg.r+10,o.bg.g+10,o.bg.b+10)screen.drawRectF(o.px,o.py,o.sx,o.sy)if in_p and iCIR(in_x,in_y,o.px,o.py,o.sx,o.sy)then o.state = true screen.setColor(o.bg.r+5,o.bg.g+5,o.bg.b+5)else o.state = false screen.setColor(o.bg.r,o.bg.g,o.bg.b)end screen.drawRectF(o.px+1,o.py+1,o.sx-2,o.sy-2)screen.setColor(o.fg.r,o.fg.g,o.fg.b)screen.drawText((o.px+o.sx/2)-string.len(o.text)*5/2+1,o.py+o.sy/2-3,o.text)end function iCIR(x,y,rX,rY,rW,rH)return x>rX-1 and y>rY-1 and x<rX+rW and y<rY+rH end
